function initNavbar(){let e=document.querySelector(".navbar"),a=document.querySelector(".navbar__burger"),r=document.querySelector(".navbar__mobile-menu");window.addEventListener("scroll",()=>{window.scrollY>50?e.classList.add("navbar--scrolled"):e.classList.remove("navbar--scrolled")}),a&&r&&a.addEventListener("click",()=>{r.classList.toggle("navbar__mobile-menu--active")})}export default initNavbar;