function initFormAnimations(){let e=document.querySelectorAll(".form-group");e.forEach(e=>{let t=e.querySelector("input, textarea, select"),a=e.querySelector("label");t.addEventListener("focus",()=>{a.classList.add("active")}),t.addEventListener("blur",()=>{t.value||a.classList.remove("active")}),t.value&&a.classList.add("active")})}export default initFormAnimations;